var Client;
var irc = require('irc');
var util = require('util');

function Client(server, nickname, opts) {
    irc.Client.apply(this, arguments);

    this.nickname = nickname;
    this.server = server;
}

util.inherits(Client, irc.Client);

Client.prototype.joinChannel = function (channel) {
    this.join(channel);
}
Client.prototype.partChannel = function (channel) {
    this.part(channel)
}
Client.prototype.cyckeChannel = function (channel) {
    this.part(channel)
    this.join(channel)
}

exports.Client = Client;
